import { AppButton } from "@components/ui/shared/AppButton";
import { IUIConfig } from "@interfaces/ui-config";
import { updateTabValue } from "@redux/ui/actions";
import React, { useState } from "react";
import { connect, useSelector } from "react-redux";

interface TrendingHeaderTabsProps {
  updateTabValue?: (tabIndex: number) => void; // Redux Action
  handleTabChange: (tab: any) => void; // Tab পরিবর্তনের হ্যান্ডলার
  activeTab:any
  setActiveTab:any
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = ({
  updateTabValue,
  handleTabChange,
}) => {
  const selectedTabIndex = useSelector((state: any) => state.ui.selectedTabIndex); // Redux থেকে state নেওয়া
  const user = useSelector((state: any) => state.user.current); // ব্যবহারকারীর তথ্য Redux থেকে নেওয়া
  const [activeTab, setActiveTab] = useState(""); // Local state

  // বাটন ক্লিক করার লজিক
  const handleButtonClick = (tabName: string, tabIndex: number) => {
    setActiveTab(tabName);
    if (selectedTabIndex === tabIndex) {
      handleTabChange(tabName); // Tab পরিবর্তনের ফাংশন কল
    }
    if (updateTabValue) {
      updateTabValue(tabIndex); // Redux Action কল
    }
  };

  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        {/* Fans Button */}
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${
            selectedTabIndex === 2 ? "" : "bg-transparent"
          }`}
          variant="tertiary"
          onClick={() => handleButtonClick("fans", 2)} // Tab সেট এবং Redux কল
        >
          <div className="flex gap-3">
            <span>Fans</span>
            <span>{user?.stats?.totalFollower}</span>
          </div>
        </AppButton>

        {/* Following Button */}
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${
            selectedTabIndex === 3 ? "" : "bg-transparent"
          }`}
          variant="tertiary"
          onClick={() => handleButtonClick("following", 3)} // Tab সেট এবং Redux কল
        >
          <div className="flex gap-3">
            <span>Following</span>
            <span>{user?.stats?.totalFollowing}</span>
          </div>
        </AppButton>
      </nav>

      {/* Fans Tab Content */}
      {activeTab === "fans" && (
        <div className="mt-5">
          <h2>Fans Information</h2>
        </div>
      )}

      {/* Following Tab Content */}
      {activeTab === "following" && (
        <div className="mt-5">
          <h2>Following Information</h2>
        </div>
      )}
    </header>
  );
};

// Redux State Map
const mapStateToProps = (state: any) => ({
  selectedTabIndex: state.ui.selectedTabIndex, // Redux থেকে সরাসরি `selectedTabIndex` নেওয়া
});

// Redux Dispatch Map
const mapDispatchToProps = {
  updateTabValue,
};

// Redux Connect
export default connect(mapStateToProps, mapDispatchToProps)(TrendingHeaderTabs);
