/* eslint-disable no-param-reassign */
import { merge } from 'lodash';
import { createReducers } from '@lib/redux';
import { onFollow } from '@redux/performer/actions';
import {
  getFeeds,
  getFeedsSuccess,
  getFeedsFail,
  moreFeeds,
  moreFeedsFail,
  moreFeedsSuccess,
  removeFeedSuccess,
  getVideoFeeds,
  getVideoFeedsSuccess,
  getVideoFeedsFail,
  moreVideoFeeds,
  moreVideoFeedsSuccess,
  moreVideoFeedsFail,
  getPhotoFeeds,
  getPhotoFeedsFail,
  morePhotoFeeds,
  morePhotoFeedsFail,
  morePhotoFeedsSuccess,
  getPhotoFeedsSuccess,
  getFollowingFeeds,
  getFollowingFeedsSuccess,
  getFollowingFeedsFail,
  moreFollowingFeeds,
  moreFollowingFeedsSuccess,
  moreFollowingFeedsFail,

  getTrendingFeeds,
  getTrendingFeedsSuccess,
  getTrendingFeedsFail,
  moreTrendingFeeds,
  moreTrendingFeedsSuccess,
  moreTrendingFeedsFail,
  removeTrendingFeedSuccess,

  getHashTagFeeds,
  getHashTagFeedsSuccess,
  getHashTagFeedsFail,
  moreHashTagFeeds,
  moreHashTagFeedsSuccess,
  moreHashTagFeedsFail,

  getRecommendFeeds,
  getRecommendFeedsSuccess,
  getRecommendFeedsFail,
  moreRecommendFeeds,
  moreRecommendFeedsSuccess,
  moreRecommendFeedsFail,
  resetFeeds,

  getRelatedFeedsSuccess,
  setMetaThumbnail
} from './actions';

const initialState = {
  feeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  recommendFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  followingFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  trendingFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },

  hashtagFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  videoFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  photoFeeds: {
    requesting: false,
    error: null,
    data: null,
    success: false
  },
  metaDataThumbnail: null

};

const feedReducers = [
  {
    on: getFeeds,
    reducer(prevState: any) {
      return {
        ...prevState,
        feeds: {
          ...initialState.feeds,
          requesting: true
        }
      };
    }
  },

  {
    on: getFeedsSuccess,
    reducer(prevState: any, data: any) {
      return {
        ...prevState,
        feeds: {
          ...prevState.feeds,
          requesting: false,
          items: data.payload.data,
          total: data.payload.total,
          success: true
        }
      };
    }
  },
  {
    on: getFeedsFail,
    reducer(prevState: any, data: any) {
      return {
        ...prevState,
        feeds: {
          ...prevState.feeds,
          requesting: false,
          error: data.payload
        }
      };
    }
  },
  {
    on: moreFeeds,
    reducer(prevState: any) {
      return {
        ...prevState,
        feeds: {
          ...prevState.feeds,
          requesting: true,
          error: null,
          success: false
        }
      };
    }
  },
  {
    on: moreFeedsSuccess,
    reducer(prevState: any, data: any) {
      return {
        ...prevState,
        feeds: {
          ...prevState.feeds,
          requesting: false,
          total: data.payload.total,
          items: [...prevState.feeds.items, ...data.payload.data],
          success: true
        }
      };
    }
  },
  {
    on: moreFeedsFail,
    reducer(prevState: any, data: any) {
      return {
        ...prevState,
        feeds: {
          ...prevState.feeds,
          requesting: false,
          error: data.payload,
          success: false
        }
      };
    }
  },
  {
    on: getFollowingFeeds,
    reducer(prevState: any) {
      return {
        ...prevState,
        followingFeeds: {
          ...initialState.followingFeeds,
          requesting: true
        }
      };
    }
  },
  {
    on: getFollowingFeedsSuccess,
