import { AppButton } from "@components/ui/shared/AppButton"; 

interface TrendingHeaderTabsProps {
  activeTab: string;
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = ({ activeTab}) => {


  const renderTabContent = () => {
    if (activeTab === "following") {
      return <div>Following Information</div>;
    }
    if (activeTab === "fans") {
      return <div>Fans Information</div>;
    }
    return null;
  };

  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "following" ? "bg-blue-500" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
          
            // router.push("/following"); 
          }}
        >
          <span>Following</span>
        </AppButton>

        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "fans" ? "bg-blue-500" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
           
            // router.push("/fans");
          }}
        >
          <span>Fans</span>
        </AppButton>
      </nav>

    
      <div className="mt-4">
        {renderTabContent()}
      </div>
    </header>
  );
};

export default TrendingHeaderTabs;
