/* eslint-disable react/require-default-props */
import PageHeader from "src/layouts/page-header";
import "./home-page-header.less";
import clsx from "clsx";
import { useState } from "react";


interface ITabProps {
  key: string;
  label?: string;
  tab: any;
  disabled?: boolean;
  icon?: JSX.Element
}
interface IProps {
  handleTabChange: Function;
  tabs: Array<ITabProps>;
  defaultTab?: string;
  className?:string
  title?:string
}

function HomePageHeader({ handleTabChange,  defaultTab, title, className }: IProps) {
  const tabs=[
                {
                  key: 'following',
                  tab: <div className="tab-follow">
                    <span>Following</span>
                    <span>
                      {/* {performer?.stats?.totalFollowing} */}
                    </span>
                  </div>
                },
                {
                  key: 'follower',
                  tab: <div className="tab-follow">
                    <span>Fans</span>
                    <span>
                      {performer?.stats?.totalFollower}
                    </span>
                  </div>
                }
              ]
  const [tba, setTba] = useState('')
  console.log(tba)
  return (
    <div className="home-page-header" id="tab-header">
      <h3 className="home_title mb-2 text-center text-black">{title}</h3>

      
      <PageHeader
        bordered={false}
        className={clsx('home_page_header rounded-full p-1', className)}
      >
        {tabs.map((item) => (
          <div
            className={`rounded-full flex items-center cursor-pointer gap-2 justify-center px-6 py-3 ${item.key === defaultTab ? 'active_width' : 'flex-1'}`}
            key={item.key}
            onClick={() => setTba(item.key)}
          >
            {item.icon} <p className="text-[#ADADAD]">{item.label}</p>
          </div>
        ))}
      </PageHeader>
    </div>
  );
}

export default HomePageHeader;
