import { CameraIcon, VideoIcon } from "src/icons"; // Assuming these icons are used elsewhere in your code
import { AppButton } from "@components/ui/shared/AppButton"; // Custom AppButton component
import { useRouter } from "next/router"; // Using Next.js router to navigate between tabs

interface TrendingHeaderTabsProps {
  activeTab: string;
  updateTab: (tag: string) => void;
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = ({ activeTab, updateTab }) => {
  const router = useRouter();

  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        {/* First Button - For "Trending Videos" */}
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "trending-videos" ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            updateTab("trending-videos"); // Update the active tab to 'trending-videos'
            router.push("/trending?tab=video"); // Navigate to the trending videos route
          }}
        >
          <span>Following</span>
        </AppButton>
        
        {/* Second Button - For "Trending Photos" */}
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "trending-photos" ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            updateTab("trending-photos"); // Update the active tab to 'trending-photos'
            router.push("/trending?tab=photo"); // Navigate to the trending photos route
          }}
        >
          <span>Fans</span>
        </AppButton>
      </nav>
    </header>
  );
};

export default TrendingHeaderTabs;
