import { AppButton } from "@components/ui/shared/AppButton";
import { IUIConfig } from "@interfaces/ui-config";
import { updateTabValue } from "@redux/ui/actions";
import React, { useCallback, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";


interface TrendingHeaderTabsProps {
  activeTab: string;
  setActiveTab: (tab: string) => void;
  ui: IUIConfig;
  user: any;
  handleTabChange: (tab: any) => void;
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = ({ handleTabChange }) => {
  const selectedTabIndex = useSelector((state: any) => state.ui.selectedTabIndex, (prev, next) => prev === next);
  const user = useSelector((state: any) => state.user.current);
  const dispatch = useDispatch();
  // console.log(selectedTabIndex)

  const handleTabClick = useCallback((tab: string, tabIndex: number) => {
    handleTabChange(tab);
    dispatch(updateTabValue(tabIndex));
  }, [handleTabChange, dispatch]);
  
  useEffect(() => {
    if (selectedTabIndex === 2) {
      handleTabChange('follower');
    }else if(selectedTabIndex ===3){
      handleTabChange('following')
    }
  }, [selectedTabIndex,handleTabChange]);
    
   
  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${
            selectedTabIndex === 2 ? "" : "bg-transparent"
          }`}
          variant="tertiary"
          onClick={() => handleTabClick("follower", 2)}
        >
          <div className="flex gap-3">
            <span>Fans</span>
            <span>{user?.stats?.totalFollower}</span>
          </div>
        </AppButton>

        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${
            selectedTabIndex === 3 ? "" : "bg-transparent"
          }`}
          variant="tertiary"
          onClick={() => handleTabClick("following", 3)}
        >
          <div className="flex gap-3">
            <span>Following</span>
            <span>{user?.stats?.totalFollowing}</span>
          </div>
        </AppButton>
      </nav>
    </header>
  );
};

export default TrendingHeaderTabs;
