import { AppButton } from "@components/ui/shared/AppButton";
import React, { useState } from "react";
interface TrendingHeaderTabsProps {
  activeTab: string;
  setActiveTab:string;
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = () => {

  const [activeTab, setActiveTab] = useState("");


  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "following" ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            setActiveTab("Fans")

          }}
        >
          <span>Fans</span>
        </AppButton>

        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${activeTab === "fans" ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            setActiveTab("fans")
          }}
        >
          <span>Fans</span>
        </AppButton>
       
      </nav>
      {activeTab === "following" && (
          <div className="mt-5">

            <h2>Following Information</h2>

          </div>
        )}
         {activeTab === "fans" && (
          <div className="mt-5">

            <h2>Fans Information</h2>

          </div>
        )}
    </header>
  );
};

export default TrendingHeaderTabs;
