function HomePageHeader({ handleTabChange, tabs, defaultTab, title, className }: IProps) {
  const [tabss, setTab] = useState<string>(defaultTab || '');

  const handle = (tab: ITabProps) => {
    setTab(tab.key);
    handleTabChange(tab.key);
  };

  console.log("Selected tab:", tabss);

  return (
    <div className="home-page-header" id="tab-header">
      <h3 className="home_title mb-2 text-center text-black">{title}</h3>

      <PageHeader
        bordered={false}
        className={clsx('home_page_header rounded-full p-1', className)}
      >
        {tabs.map((tab) => (
          <div
            className={`rounded-full flex items-center cursor-pointer gap-2 justify-center px-6 py-3 ${
              tab.key === tabss ? 'active_width' : 'flex-1'
            }`}
            key={tab.key}
            onClick={() => handle(tab)}
          >
            {tab.icon} <p className="text-[#ADADAD]">{tab.label}</p>
          </div>
        ))}
      </PageHeader>
    </div>
  );
}
