                            <RubyIcon className="w-5 h-5" />
                            <span className="text-sm font-light">
                              {Number.isInteger(user?.rubyBalance)
                                ? user?.rubyBalance
                                : Math.floor(user?.rubyBalance * 10) / 10}
                            </span>
                          </div>
                        </div>
                      </a>
                    </button>
                  </div>


                  {user?.balance > 0 && (
                    <div className="w-1/2">
                      <button
                        onClick={() =>
                          this.handleNavigate("/wallet?diamond", 1)

                        }
                        className='pl-4'
                      >
                        <a className="mb-1 text-base font-semibold leading-4 text-black">
                          Diamonds
                          <div className="flex items-center gap-1">
                            <DiamondIcon className="w-5 h-5" />
                            <span className="text-sm font-light">
                              {Number.isInteger(user?.balance)
                                ? user?.balance
                                : Math.floor(user?.balance * 10) / 10}
                            </span>
                          </div>
                        </a>
                      </button>
                    </div>
                  )}
                </div>


                <div className="flex  items-center ">

                  {!user?.roles?.includes(
                    ROLE_PERMISSIONS.ROLE_HOST_PRIVATE
                  ) && (
                      <div className="w-1/2 border-r-1 border-base-400">
                        <div>
                        <button onClick={() => this.handleNavigate("/following-fans?fans", 2)} className="mb-1 text-sm font-semibold leading-4 text-black">
                           <div>
                           <p >Fans</p>
                            <p className="text-sm font-light text-left">
                              {user?.stats?.totalFollower}
                            </p>
                           </div>
                          </button>
                         
                        </div>
                        <button  >
                        </button>
                      </div>
                    )}


                  <div className="w-1/2 pl-4">
                    <div className="mt-[-1.5em]">
                      <button onClick={() => this.handleNavigate("/following-fans?following", 3)} className="mb-1 text-sm font-semibold leading-4 text-black">
