import { AppButton } from "@components/ui/shared/AppButton";
import { IUIConfig } from "@interfaces/ui-config";
import React, { useState } from "react";
import { connect, useSelector } from "react-redux";

interface TrendingHeaderTabsProps {
  activeTab: string;
  setActiveTab: (tab: string) => void;
  ui:IUIConfig
}

const TrendingHeaderTabs: React.FC<TrendingHeaderTabsProps> = () => {
  const selectedTabIndex = useSelector((state:any)=>state.ui.selectedTabIndex)
  const user = useSelector((state:any)=>state.user.current)
  const [activeTab, setActiveTab] = useState("");


  return (
    <header className="pb-3 flex flex-col items-center justify-center gap-2 w-full flex-wrap">
      <nav className="w-full md:w-auto bg-gray-200 p-1 rounded-full text-center flex items-center justify-center">
        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${selectedTabIndex === 2 ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            setActiveTab("fans");
          }}
        >
          <span>Fans {selectedTabIndex === 2 && user?.stats?.totalFollower}</span>
        </AppButton>

        <AppButton
          className={`flex-1 border-0 flex items-center justify-center ${selectedTabIndex === 3 ? "" : "bg-transparent"}`}
          variant="tertiary"
          onClick={() => {
            setActiveTab("following");
          }}
        >
          <span>Following {selectedTabIndex ===  3 && user?.stats?.totalFollowing}</span>
        </AppButton>
      </nav>

      {activeTab === "fans" && (
        <div className="mt-5">
          <h2>Fans Information</h2>
        </div>
      )}
      
      {activeTab === "following" && (
        <div className="mt-5">
          <h2>Following Information</h2>
        </div>
      )}
    </header>
  );
};

const mapStates = (state:any)=>({
  selectedTabIndex:{...state.ui}
})

const mapDispatch ={}
export default connect(mapStates, mapDispatch)(TrendingHeaderTabs);
